const Form = ({ person, setPerson, resetForm }) => {

    const handlerInput = (ev) => {
        const idInput = ev.target.id;
        const valueInput = ev.target.value;
        setPerson({
            ...person,
            [idInput]: valueInput
        })
        console.log(person)
    }
    const submitForm = (ev) => {
        ev.preventDefault()
    }
    const handlerReset = () => {
        resetForm();
    }
    return (
        <form onSubmit={submitForm}>
            <label htmlFor="name"></label>
            <input type="text" id="name" onChange={handlerInput} /> <br />
            <label htmlFor="lastname"></label>
            <input type="text" id="lastname" onChange={handlerInput} /> <br />
            <input type="submit" value="ENVIAR" />
            <input type="button" value="RESET" onClick={handlerReset} />
        </form>
    )

}
export default Form
